        <script type="text/javascript">
            var alamat_before   = Array();
            var alamat_baru     = Array();
            var array_tarif     = Array();
            $(function () {                
                function clickSound() {
                    try {
                        window.cpjs.clickSound();                        
                    } catch(err) {
                        console.log(err.message);
                    }
                }
                
                /*$('[name=nama_alamat]').keyup(function () { 
